import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';

const styles = theme => ({
    root: {
      minWidth: '150px',
      marginRight: '5rem',
      marginTop: '2rem',
    },
    media: {
        height: 200,
      },
    centerText: {
        textAlign: "center",
      },
    spacing: {
      padding: theme.spacing.unit * 2,
    },
  });

function MediaCard(props) {
  const { classes } = props;
  return (
    <div className={classes.root}>
    <Card className={classes.card}>
        <CardMedia
          className={classes.media}
          image="http://lorempixel.com/output/people-q-c-250-100-1.jpg"
          title="Contemplative Reptile"
        />
        <CardContent>
        <Typography gutterBottom variant="h5" component="p" className={classes.centerText}>
            <strong>John Doe</strong>
          </Typography>
          <Typography gutterBottom variant="h6" component="p" className={classes.centerText}>
            Following
          </Typography>
          <Typography component="p" className={classes.centerText}>
              100
          </Typography>
          <Typography gutterBottom variant="h6" component="p" className={classes.centerText}>
            Followers
          </Typography>
          <Typography component="p" className={classes.centerText}>
              100
          </Typography>
          </CardContent>
          <CardContent>
          <Grid container spacing={8} style={{justifyContent: "center"}}>
          <Grid item>
          <Button variant="contained" color="primary">
          Subscribe
          </Button>
          </Grid>
          <Grid item>
          <Button variant="contained" color="secondary">
            Donate
          </Button>
          </Grid>
          </Grid>
        </CardContent>
    </Card>
    </div>
  );
}

MediaCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MediaCard);
