import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
//import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';

const styles = theme => ({
    card: {
        minWidth: 275,
      },
      media: {
        height: 140,
      },
      center: {
          textAlign: 'center',
      },
    spacing: {
      padding: theme.spacing.unit * 2,
    },
  });

function MediaCard(props) {
  const { classes } = props;
  return (
    <Grid container spacing={24}>
    <Grid item xs={2}>
    <Card className={classes.card}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="http://lorempixel.com/output/people-q-c-250-100-1.jpg"
          title="Contemplative Reptile"
        />
        <CardContent>
        <Typography gutterBottom variant="h4" component="p" className={classes.center}>
            Adrian Morales
          </Typography>
          <Typography gutterBottom variant="h6" component="p" className={classes.center}>
            Following
          </Typography>
          <Typography component="p" className={classes.center}>
              100
          </Typography>
          <Typography gutterBottom variant="h6" component="p" className={classes.center}>
            Followers
          </Typography>
          <Typography component="p" className={classes.center}>
              100
          </Typography>
          <Grid container spacing={13}>
          <Grid item>
          <Button variant="contained" color="primary">
          Subscribe
        </Button>
        </Grid>
        <Grid item>
        <Button variant="contained" color="secondary">
          Donate
        </Button>
        </Grid>
        </Grid>
        </CardContent>
      </CardActionArea>
    </Card>
    </Grid>
    </Grid>
  );
}

MediaCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MediaCard);
